'use strict';

const _require = require('buffer'),
      Buffer = _require.Buffer;

const configure = require('../lib/configure');

const encodeBuffer = require('../lib/encode-buffer-uri-component');

module.exports = configure(({
  ky
}) => {
  return async (topic, data, options) => {
    options = options || {};
    data = Buffer.from(data);
    const searchParams = new URLSearchParams(options.searchParams);
    searchParams.set('arg', topic);
    const res = await ky.post("pubsub/pub?".concat(searchParams, "&arg=").concat(encodeBuffer(data)), {
      timeout: options.timeout,
      signal: options.signal,
      headers: options.headers
    }).text();
    return res;
  };
});
