const React = require('react')
import Button from 'antd/lib/button';
const ipfsClient = require('ipfs-http-client')

const ipfs = ipfsClient('/ip4/127.0.0.1/tcp/5001')
const stringToUse = 'hello world from webpacked IPFS'

class Product extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
      isSuccess: null,
      id: null,
      version: null,
      protocol_version: null,
      added_file_hash: null,
      added_file_contents: null
    }
  }
  async componentDidMount () {
    const id = await ipfs.id()
    this.setState({
      id: id.id,
      version: id.agentVersion,
      protocol_version: id.protocolVersion
    })
  }

  uploadIpfs = async() => {
  
    const source = ipfs.add(stringToUse)
    for await (const file of source) {
      console.log("TCL: App -> forawait -> file", file)
      const hash = file.path
      this.setState({ added_file_hash: hash, isSuccess: "成功" })
      console.log("file_hash: ",hash)

      const source = ipfs.cat(hash)
      const data = []
      for await (const chunk of source) {
        data.push(chunk)
      }
      this.setState({ added_file_contents: Buffer.concat(data).toString() })
      console.log("file_contents: ",Buffer.concat(data).toString())
    }
  }
  render () {
    return <div style={{ textAlign: 'center' }}>
      <Button type="primary" onClick={this.uploadIpfs}>上传至IPFS</Button>
      <p>上传状态： <strong>{this.state.isSuccess}</strong></p>
      <h1>Everything is working!</h1>
      <p>Your ID is <strong>{this.state.id}</strong></p>
      <p>Your IPFS version is <strong>{this.state.version}</strong></p>
      <p>Your IPFS protocol version is <strong>{this.state.protocol_version}</strong></p>
      <div>
        <div>
          Added a file! <br />
          {this.state.added_file_hash}
        </div>
        <div>
          Contents of this file: <br />
          {this.state.added_file_contents}
        </div>
      </div>
    </div>
  }
}
module.exports = Product


